package Demo01;

public class Persion {

	private int age;
	private String name;

	public Persion() {
		super();
		// TODO Auto-generated constructor stub
	}

	public Persion(int age, String name) {
		super();
		this.age = age;
		this.name = name;
	}

	public int getAge() {
		return age;
	}

	public void setAge(int age) {
		this.age = age;
	}

	public String getName() {
		return name;
	}

	public void setName(String name) {
		this.name = name;
	}
	
}
